mapper.select:
select 

from GDZC.TD_ASSETS_EXTEND
where ASSETS_RECORD = #{assetsRecord,jdbcType=VARCHAR}
  and PROJECT_NO = #{projectNo,jdbcType=VARCHAR}

mapper.delete:
delete from GDZC.TD_ASSETS_EXTEND
where ASSETS_RECORD = #{assetsRecord,jdbcType=VARCHAR}
  and PROJECT_NO = #{projectNo,jdbcType=VARCHAR}
