mapper.select:
select a.assets_record_m "assetsRecordGuid" , a.assets_record "assetsRecord" , e.equipment_standard_name "equipmentStandardName" , a.use_fstate "useFstate" , e.spec "spec" , a.product_type "productType" , a.custodian "custodian" , a.use_dept_GUID "useDeptCode" , (select dept_name from td_org_dept where dept_guid = a.use_dept_guid) "useDept" , a.b_dept_GUID "bDeptCode" , (select dept_name from td_org_dept where dept_guid = a.b_dept_guid) "bDept" from td_assets_record a left join td_equipment e on (a.equipment_code = e.equipment_code) where 1 = 1
and ( regexp_like(a.assets_record, #{conditiions.mobile}) or regexp_like(e.equipment_standard_name, #{conditiions.mobile}) )
order by "${conditiions.orderField}" ${conditiions.orderMark}

mapper.insert:
insert into td_assets_record (ASSETS_RECORD_M, ASSETS_RECORD, USE_FSTATE, PRODUCT_TYPE, USE_DEPT_GUID, CUSTODIAN, DEPOSIT, B_DEPT_GUID, CERT_GUID, TF_BRAND, PRODUCT, PRODUCT_COUNTRY, PRODUCTION_DATE, F_ORG_ID, BUY_DATE, CONTRACT_NO, BUY_PRICE, QRCODE, INSTALL_PRICE, SOURCE_FUNDS, RRPAIR_TYPE, MAINTAIN_TYPE, METERING_TYPE, MAINTAIN_DAY, NEXT_MAINTAIN_DATE, IN_DATE, DEPRECIATION_TYPE, ORIGINAL_VALUE, CARRYING_AMOUNT, RESIDUAL_VALUE, RESIDUAL_VALUE_V, USE_LIMIT, CREATE_USERID, ORG_ID , EQUIPMENT_CODE, RECODR_DATE
) select * from
select sys_guid(), #{list[${index}].assetsRecord,jdbcType=VARCHAR}, #{list[${index}].useFstate,jdbcType=VARCHAR}, #{list[${index}].productType,jdbcType=VARCHAR}, #{list[${index}].useDeptCode,jdbcType=VARCHAR}, #{list[${index}].custodian,jdbcType=VARCHAR}, #{list[${index}].deposit,jdbcType=VARCHAR}, #{list[${index}].bDeptCode,jdbcType=VARCHAR}, #{list[${index}].certGuid,jdbcType=VARCHAR}, #{list[${index}].tfBrand,jdbcType=VARCHAR}, #{list[${index}].product,jdbcType=VARCHAR}, #{list[${index}].productCountry,jdbcType=VARCHAR}, #{list[${index}].productionDate,jdbcType=TIMESTAMP}, #{list[${index}].fOrgId,jdbcType=TIMESTAMP}, #{list[${index}].buyDate,jdbcType=TIMESTAMP}, #{list[${index}].contractNo,jdbcType=TIMESTAMP}, #{list[${index}].buyPrice,jdbcType=VARCHAR}, #{list[${index}].qrCode,jdbcType=VARCHAR} , #{list[${index}].installPrice,jdbcType=VARCHAR}, #{list[${index}].sourceFunds,jdbcType=VARCHAR}, #{list[${index}].rrpairType,jdbcType=VARCHAR}, #{list[${index}].maintainType,jdbcType=VARCHAR}, #{list[${index}].meteringType,jdbcType=VARCHAR}, #{list[${index}].maintainDay,jdbcType=VARCHAR}, #{list[${index}].nextMaintainDate,jdbcType=VARCHAR}, #{list[${index}].inDate,jdbcType=VARCHAR}, #{list[${index}].depreciationType,jdbcType=VARCHAR}, #{list[${index}].originalValue,jdbcType=VARCHAR}, #{list[${index}].carryingAmount,jdbcType=VARCHAR}, #{list[${index}].residualValue,jdbcType=VARCHAR}, #{list[${index}].residualValueV,jdbcType=VARCHAR}, #{list[${index}].useLimit,jdbcType=DECIMAL}, #{list[${index}].createUserid,jdbcType=VARCHAR}, #{list[${index}].orgId,jdbcType=DECIMAL}, #{list[${index}].equipmentCode,jdbcType=VARCHAR} , sysdate from dual
