mapper.select:
select ro.rrpair_order_guid "rrpairOrderGuid",--维修工单号guid ro.rrpair_order_no "rrpairOrderNo" , -- 维修单号 ro.order_fstate "orderFstate", -- 单据状态：待接修，维修中,待验收，已关闭 tdi.dept_name "deptName",--ro.use_dept_code "useDeptCode",--TD_ORG_DEPT.DEPT_NAME（使用科室） ro.rrpair_userid "rrpairUserid", --报修人id ro.rrpair_username "rrpairUsername", --报修人姓名 ro.rrpair_phone "rrpairPhone", -- 报修人电话 ro.rrpair_type "rrpairType", --维修方式:内修，外修 ro.in_rrpair_userid "inRrpairUserid", --内修负责人id ro.in_rrpair_username "inRrpairUsername", --内修负责人姓名 ro.out_rrpair_username "outRrpairUsername", --外修负责人姓名 ro.create_date "createDate", --创建时间-报修时间（报修时间） ro.rrpair_date "rrpairDate",--修复日期(完成时间) ro.assets_record "assetsRecord", -- 资产档案号-资产编号 ro.order_type "orderType", -- 工单性质：报修登记（科室和补报修），保养后发现，巡检后发现(报修来源) ro.urgent_flag "urgentFlag", --紧急度：紧急，急，一般(紧急度) ro.spare "spare", --备用机：无，有(备用) ro.guarantee_flag "guaranteeFlag", --保修：在保，出保(在保) ro.rrpair_send "rrpairSend", --是否送修：是，否(是否送修) ro.repair_content_typ "repairContentTyp", --故障原因（故障描述） ro.call_time "callTime", --指派时间 ro.out_org "outOrg", --外修机构(维修单位) ro.actual_price "actualPrice", -- 实际费用(维修费用) --更换配件(占时不用) (select dept_name from td_org_dept t where t.dept_code=roa.dept_code) checkDeptNam, --验收科室 roa.rr_acce_userid "rrAcceUserid", --报修后验收人id roa.rr_acce_username "rrAcceUsername", --报修后验收人姓名 roa.rr_acce_fstate "rrAcceFstate", --报修后验收人验收 60已验收 90验收不通过（验收结果） roa.rr_acce_date "rrAcceAate", --报修后验收日期 （验收日期）
te.equipment_standard_name "equipmentStandardName"，--equipment_code资产名称 te.spec "spec",--规格 te.fmodel "fmodel",--型号 tar.product_type "productType",--资产分类id(资产类别 ) tar.custodian "custodian",--保管员 (select dept_name from td_org_dept t where t.dept_code=tar.m_dept_code) "mDeptName", --管理科室 tar.deposit "deposit"--放地点-a区b楼c层d房间(存放地点) from td_rrpair_order ro left join tb_rrpair_order_acce roa on ro.rrpair_order_no=roa.rrpair_order left join td_equipment te on ro.equipment_code=te.equipment_code left join td_assets_record tar on ro.assets_record=tar.assets_record left join td_org_dept tdi on tar.b_dept_code=tdi.dept_code --left join td_rrpair_fitting_use rfu on ro.rrpair_order_guid=rfu.rrpair_order_guid where 1=1
and ro.rrpair_order_no=#{conditiions.rrpairOrderNo, jdbcType=VARCHAR}
and ro.rrpair_order_guid=#{conditiions.rrpairOrderGuid, jdbcType=VARCHAR}
order by "${conditiions.orderField}" ${conditiions.orderMark}

select te.ts_eq_operation_gu_id "tsEqOperationGuId",tui.user_name "userName",te.op_type_sum "opTypeSum",te.createtime "createtime",te.tf_remark "tfRemark"
from ts_eq_operation te
left join ts_user_info tui on te.user_id=tui.user_id
where 1=1

    and te.op_type_sum=#{conditiions.opTypeSum, jdbcType=VARCHAR}


    and tui.user_name=#{conditiions.userName, jdbcType=VARCHAR}


    and regexp_like(te.op_type_sum, #{conditiions.opTypeSum, jdbcType=VARCHAR}) OR
        regexp_like(tui.user_name, #{conditiions.userName, jdbcType=VARCHAR})


    order by "${conditiions.orderField}" ${conditiions.orderMark}

select ro.rrpair_order_guid "rrpairOrderGuid",--维修工单号guid ro.rrpair_order_no "rrpairOrderNo" , -- 维修单号 ro.order_fstate "orderFstate", -- 单据状态：待接修，维修中,待验收，已关闭 te.equipment_standard_name "equipmentStandardName"，--equipment_code资产名称 tdi.dept_name "deptName",--ro.use_dept_code "useDeptCode",--TD_ORG_DEPT.DEPT_NAME（使用科室） tar.custodian "custodian",--保管员 ro.rrpair_userid "rrpairUserid", --报修人id ro.rrpair_username "rrpairUsername", --报修人姓名 ro.create_date "createDate", --创建时间-报修时间（报修时间，维修时间） ro.rrpair_type "rrpairType", --维修方式:内修，外修 ro.in_rrpair_userid "inRrpairUserid", --内修负责人id ro.in_rrpair_username "inRrpairUsername", --内修负责人姓名 ro.out_rrpair_username "outRrpairUsername", --外修负责人姓名 ro.fault_describe "faultDescribe", --故障现象 ro.urgent_flag "urgentFlag", --紧急度：紧急，急，一般(紧急度) ro.repair_result "repairResult" --维修结果 from td_rrpair_order ro left join td_equipment te on ro.equipment_code=te.equipment_code left join td_assets_record tar on ro.assets_record=tar.assets_record left join td_org_dept tdi on tar.b_dept_code=tdi.dept_code where 1=1
and regexp_like(ro.rrpair_order_no, #{conditiions.params, jdbcType=VARCHAR}) OR regexp_like(ro.assets_record, #{conditiions.params, jdbcType=VARCHAR}) OR regexp_like(te.equipment_standard_name, #{conditiions.params, jdbcType=VARCHAR})
and ro.order_fstate = #{conditiions.orderFstate, jdbcType=VARCHAR}
order by "${conditiions.orderField}" ${conditiions.orderMark}
order by ro.create_date desc
