mapper.select:
select t.TF_CLO_CODE,t.TF_CLO_NAME 
from TD_STATIC_INFO i,TD_STATIC_DATA t 
where i.static_id = t.static_id and i.STATIC_TYPE = '00'

	and i.TF_TABLE = #{conditiions.tableName,jdbcType=VARCHAR}


	and i.TF_CLO = #{conditiions.filedName,jdbcType=VARCHAR}


	and t.TF_CLO_CODE = #{conditiions.tfCloCode,jdbcType=VARCHAR}


	and t.TF_CLO_NAME = #{conditiions.tfCloName,jdbcType=VARCHAR}

order by t.TF_CLO_CODE

SELECT tsd.TF_CLO_CODE, tsd.TF_CLO_NAME FROM TD_STATIC_INFO tsi JOIN TD_STATIC_DATA tsd on tsi.STATIC_ID=tsd.STATIC_ID WHERE 1=1
and tsi.ORG_ID=#{conditiions.orgId,jdbcType=DECIMAL}
and tsi.TF_CLO=#{conditiions.tfClo,jdbcType=VARCHAR}
ORDER BY to_number(tsd.TF_CLO_CODE) asc

select from TD_STATIC_DATA t where t.static_id = #{conditiions.staticId}
and (regexp_like(t.TF_CLO_CODE, #{conditiions.searchParams, jdbcType=VARCHAR}) or regexp_like(t.TF_CLO_NAME, #{conditiions.searchParams, jdbcType=VARCHAR}))
order by t.TF_CLO_CODE

select COUNT(*) from (
	select d.TF_CLO_CODE from td_static_data d 
	where d.TF_CLO_CODE=#{tfCloCode} and d.static_id = #{staticId}
	  
		  	and d.STATIC_DATA_GUID != #{staticDataGuid,jdbcType=VARCHAR}
	  
)

select t.TF_CLO_CODE,t.TF_CLO_NAME 
from TD_STATIC_INFO i,TD_STATIC_DATA t 
where i.static_id = t.static_id 

	and i.TF_TABLE = #{conditiions.tableName,jdbcType=VARCHAR}


	and i.TF_CLO = #{conditiions.filedName,jdbcType=VARCHAR}


	and i.ORG_ID = #{conditiions.orgId,jdbcType=VARCHAR}

order by t.TF_CLO_CODE

SELECT consts.name names FROM (
select #{names[${index}], jdbcType=VARCHAR} name FROM dual
) consts left JOIN ( SELECT DISTINCT cd.TF_CLO_NAME name FROM TD_STATIC_INFO cn JOIN TD_STATIC_DATA cd ON cn.STATIC_ID=cd.STATIC_ID WHERE cn.TF_CLO = #{dictCode, jdbcType=VARCHAR} ) names ON consts.name=names.name WHERE names.name is NULL

SELECT consts.name names FROM (
select #{names[${index}], jdbcType=VARCHAR} name FROM dual
) consts left JOIN ( SELECT DISTINCT cn.TF_CLO name FROM TD_STATIC_INFO cn,TD_STATIC_INFO pcn WHERE cn.PARENT_STATIC_ID = pcn.STATIC_ID and pcn.TF_CLO = #{dictCode, jdbcType=VARCHAR} ) names ON consts.name=names.name WHERE names.name is NULL
