mapper.select:
select i.STATIC_ID "staticId",i.TF_CLO "tfClo",i.TF_COMMENT "tfComment", i.FSORT "fsort",i.PARENT_STATIC_ID "parentStaticId" from td_static_info i where i.ORG_ID = #{conditiions.sessionOrgId, jdbcType=VARCHAR}
and (regexp_like(i.TF_COMMENT, #{conditiions.searchParams, jdbcType=VARCHAR}) or regexp_like(i.TF_CLO, #{conditiions.searchParams, jdbcType=VARCHAR}))
order by i.fsort

select COUNT(*) from (
	select i.TF_CLO from td_static_info i 
	where i.TF_CLO = #{tfClo,jdbcType=VARCHAR}
		  and i.ORG_ID = #{orgId,jdbcType=VARCHAR}
		  
		  	and i.static_id != #{staticId,jdbcType=VARCHAR}
		  
)

SELECT DISTINCT i.STATIC_ID "value", i.TF_COMMENT "text" FROM td_static_info i where i.ORG_ID = #{conditiions.orgId, jdbcType=VARCHAR}
and (regexp_like(i.TF_COMMENT, #{conditiions.searchParams, jdbcType=VARCHAR}) or regexp_like(i.TF_CLO, #{conditiions.searchParams, jdbcType=VARCHAR}))
order by i.fsort
